testRunner.testCase.setPropertyValue("versionOne", "1.0.0")
testRunner.testCase.setPropertyValue("versionTwo", "2.0.0")
testRunner.testCase.setPropertyValue("description", "Test")
testRunner.testCase.setPropertyValue("description2", "Testing Updating a Deployment")
testRunner.testCase.setPropertyValue("isEnabledTrue", "true")
testRunner.testCase.setPropertyValue("isEnabledFalse", "false")


