int totalCountInResponse = json.metadata.totalCount

if (json.metadata.totalCount > json.metadata.pageSize) {
	totalCountInResponse = json.metadata.pageSize
}

if (totalCountInResponse > 0) {
	context.tenantAssociatedToDeployment = true
}


for (int i = 0; i < totalCountInResponse; i++) {

	//log.info "deployment from JSON[" + i + "]: " + json.deployments[i].key
	//log.info "expected deployment: " + context.deploymentsList[i]

	assert(json.deployments[i].key.equals(context.deploymentsList[i]))
}


