	log.info "Deployments for Tenant " + testRunner.testCase.getPropertyValue("tenantKey") + " were found"
	
} else {
	log.info "Deployments for Tenant " + testRunner.testCase.getPropertyValue("tenantKey") + " were NOT found"
	
}

