import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("GetAllDeploymentsSorted") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response


context.listDeploymentsObject = []

for(int i = 0; i < json.deployments.size(); i++) {

	def key = json.deployments[i].key
	def version = json.deployments[i].version
	def applicationKey = json.deployments[i].application.key
	def applicationName = json.deployments[i].application.name
	def enabled = json.deployments[i].enabled

	context.listDeploymentsObject.add(new DeploymentObject(key, version, applicationKey, applicationName, enabled))	
	// log.info " Deployment # " + (i+1)
	// context.listDeploymentsObject.get(i).showDeployment(log)

}

public class DeploymentObject {
	
	def key = ""
	def version = ""
	def applicationKey = ""
	def applicationName = ""
	def enabled = ""
	
	public DeploymentObject(def key, def version, def applicationKey, def applicationName, def enabled) {
		
		this.key = key.trim()
		this.version = version.trim()
		this.applicationKey = applicationKey.trim()
		this.applicationName = applicationName.trim()
		this.enabled = enabled
		
	}

	public showDeployment(log) {
		
		log.info "Deployment Key: " + key
		log.info "Deployment Version: " + version
		log.info "Application Key: " + applicationKey
		log.info "Application Name: " + applicationName
		log.info "Enabled: " + enabled
		
	}
}

import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("SortDeployments") 
def dataSourceSortBy = testCase.getTestStepByName("DataSource_SortCriteria") 
def dataSourcePage = testCase.getTestStepByName("DataSource_PageSize") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response

def sortDirection = dataSourceSortBy.getPropertyValue("sortDirection")
def sortBy = dataSourceSortBy.getPropertyValue("sortBy")
int pageSize = Integer.parseInt(dataSourcePage.getPropertyValue("pageSize"))
int page = Integer.parseInt(testCase.getPropertyValue("currentPageNumber"))

// Calculate indexes to get deployments from general list (list that contains all the deployments)
// Example: page = 2, pageSize = 5
// indexStart = (2-1) * 5 = 5
// indexEnd = 5 + 5 = 10 

int indexStart = (page - 1) * pageSize
int indexEnd = indexStart + pageSize - 1
int totalCount = Integer.parseInt(testRunner.testCase.getPropertyValue("totalCountDeployments"))

if (indexEnd >  totalCount) indexEnd = totalCount - 1

//log.info "indexStart: " + indexStart
//log.info "indexEnd: " + indexEnd
log.info "  Sort By " + sortBy + " in " + sortDirection + " order"

int index = 0

for (int i = indexStart; i <= indexEnd; i++) {

	if (sortBy == "KEY"){

		assert(json.deployments[index].key.equals(context.listDeploymentsObject.get(i).key))	
		// log.info  "  " + json.deployments[index].key + " equals " + context.listDeploymentsObject.get(i).key
		/*
		try {
			
			assert(json.deployments[index].key.equals(context.listDeploymentsObject.get(i).key))	
		
		} catch (AssertionError e) {
		
			log.info "  Deployments didn't match on page " + page
			log.info "  Deployment key expected: " + context.listDeploymentsObject.get(i).key
			log.info "  Deployment key actual  : " + json.deployments[index].key
		
		}
		*/
	
	} else if (sortBy == "VERSION"){

		String expected = context.listDeploymentsObject.get(i).version.trim()
		String actual = json.deployments[index].version.trim()
			
		assert(actual.equals(expected))
		// log.info  "  " + actual + " equals " + expected
		
		/*	
		try {
		
			assert(actual.equals(expected))
					
		} catch (AssertionError e) {
		
			log.info "  Deployments didn't match on page " + page
			log.info "  Deployment version expected: " + context.listDeploymentsObject.get(i).version
			log.info "  Deployment version actual  : " + json.deployments[index].version
		
		}*/
		
	} else if (sortBy == "APPLICATION_KEY"){

		String expected = context.listDeploymentsObject.get(i).applicationKey.trim()
		String actual = json.deployments[index].application.key.trim()
			
		assert(actual.equals(expected))
		// log.info  "  " + actual + " equals " + expected
		
		/*
		try {

			assert(actual.equals(expected))
		
		} catch (AssertionError e) {
		
			log.info "  Deployments didn't match on page " + page
			log.info "  Application key expected: " + context.listDeploymentsObject.get(i).applicationKey
			log.info "  Application key actual  : " + json.deployments[index].application.key
		
		}*/
		
	} else if (sortBy == "APPLICATION_NAME"){

		String expected = context.listDeploymentsObject.get(i).applicationName.trim()
		String actual = json.deployments[index].application.name.trim()
			
		assert(actual.equals(expected))
		// log.info  "  " + actual + " equals " + expected
		

		/*try {
		
			assert(actual.equals(expected))
		
		} catch (AssertionError e) {
		
			log.info "  Deployments didn't match on page " + page
			log.info "  Application name expected: " + context.listDeploymentsObject.get(i).applicationName
			log.info "  Application name actual  : " + json.deployments[index].application.name
		
		}*/
				
	} else if (sortBy == "IS_ENABLED"){

		assert(json.deployments[index].enabled.equals(context.listDeploymentsObject.get(i).enabled))	
		// log.info  "  " + json.deployments[index].enabled + " equals " + context.listDeploymentsObject.get(i).enabled
		
		/*try {
		
			assert(json.deployments[index].enabled.equals(context.listDeploymentsObject.get(i).enabled))	
		
		} catch (AssertionError e) {
		
			log.info "  Deployments didn't match on page " + page
			log.info "  Enabled value expected: " + context.listDeploymentsObject.get(i).enabled
			log.info "  Enabled value actual  : " + json.deployments[index].enabled
		
		}*/
		
	} 
		
	index++
}
