for(int i = 0; i < json.routes.size(); i++) {
def key = json.routes[i].key def deploymentKey = json.routes[i].deployment.key def deploymentVersion = json.routes[i].deployment.version def applicationKey = json.routes[i].application.key def applicationName = json.routes[i].application.name def tenantKey = json.routes[i].tenant.key def tenantName = json.routes[i].tenant.name def qualifier = json.routes[i].qualifier def enabled = json.routes[i].enabled
context.listRoutesObject.add(new RouteObject(key, deploymentKey, deploymentVersion, applicationKey, applicationName, tenantKey, tenantName, qualifier, enabled)) // log.info " Route # " + (i+1) // context.listRoutesObject.get(i).showRoute(log)
}
public class RouteObject {
def key = "" def deploymentKey = "" def deploymentVersion = "" def applicationKey = "" def applicationName = "" def tenantKey = "" def tenantName = "" def qualifier = "" def enabled = ""
public RouteObject(def key, def deploymentKey, def deploymentVersion, def applicationKey, def applicationName, def tenantKey, def tenantName, def qualifier, def enabled) {
this.key = key.trim() this.deploymentKey = deploymentKey.trim() this.deploymentVersion = deploymentVersion.trim() this.applicationKey = applicationKey.trim() this.applicationName = applicationName.trim() this.tenantKey = tenantKey.trim() this.tenantName = tenantName.trim() this.qualifier = qualifier.trim() this.enabled = enabled
}
public showRoute(log) {
log.info "Route Key: " + key log.info "Deployment Key: " + deploymentKey log.info "Deployment Version: " + deploymentVersion log.info "Application Key: " + applicationKey log.info "Application Name: " + applicationName log.info "Tenant Key: " + tenantKey log.info "Tenant Name: " + tenantName log.info "Qualifier: " + qualifier log.info "Enabled: " + enabled
} }

import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("SortRoutes") 
def dataSourceSortBy = testCase.getTestStepByName("DataSource_SortCriteria") 
def dataSourcePage = testCase.getTestStepByName("DataSource_PageSize") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response

def sortDirection = dataSourceSortBy.getPropertyValue("sortDirection")
def sortBy = dataSourceSortBy.getPropertyValue("sortBy")
int pageSize = Integer.parseInt(dataSourcePage.getPropertyValue("pageSize"))
int page = Integer.parseInt(testCase.getPropertyValue("currentPageNumber"))

// Calculate indexes to get routes from general list (list that contains all the routes)
// Example: page = 2, pageSize = 5
// indexStart = (2-1) * 5 = 5
// indexEnd = 5 + 5 = 10 

int indexStart = (page - 1) * pageSize
int indexEnd = indexStart + pageSize - 1
int totalCount = Integer.parseInt(testRunner.testCase.getPropertyValue("totalCountRoutes"))

if (indexEnd >  totalCount) indexEnd = totalCount - 1

//log.info "indexStart: " + indexStart
//log.info "indexEnd: " + indexEnd
log.info "  Sort By " + sortBy + " in " + sortDirection + " order"

int index = 0

for (int i = indexStart; i <= indexEnd; i++) {

