import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("SortTenants") 
def dataSource = testCase.getTestStepByName("DataSource") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response

def sortDirection = dataSource.getPropertyValue("sortDirection")
def sortBy = dataSource.getPropertyValue("sortBy")

verifyOrderSortAscending(json, sortDirection, sortBy)


// Method definition

void verifyOrderSortAscending(json, sortDirection, sortBy){

	def originalList = []
	def sortedList = []

	
	if (sortBy == "KEY"){
		originalList = json.tenants.key
	}
	else if (sortBy == "NAME"){
		originalList = json.tenants.name
	}
	else if (sortBy == "IS_ENABLED"){
		
		originalList = json.tenants.enabled
		
		for (int i = 0; i < originalList.size(); i++) {
			originalList[i] = Boolean.toString(originalList[i])
		}
	}						


	// Set to lower case all of the items in the original list
	for(int i = 0; i < originalList.size(); i++){
		originalList[i] = originalList[i].toLowerCase()
	}

	// Copy the elements from original list to sorted list
	for(int i = 0; i < originalList.size(); i++){
		sortedList[i] = originalList[i]
	}

	// The criteria to sort the list must be case insensitive. 
	Collections.sort(sortedList, String.CASE_INSENSITIVE_ORDER)

	// If sort = descending, sort list in reverse order
	if(sortDirection.equals("DESC")){
		sortedList = sortedList.reverse()
	}

	log.info "  Sort By: " + sortBy + "  -- Sort Direction: " + sortDirection
	log.info "  Original List: " + originalList
	log.info "  Sorted List:   " + sortedList
	log.info ""
	
	assert(sortedList.equals(originalList))
	
}

