int pageSize = Integer.parseInt(dataSource.getPropertyValue("pageSize"))

int pagesAmount = totalCountTenants / pageSize

if (totalCountTenants % pageSize > 0) 
	pagesAmount = totalCountTenants / pageSize + 1


testRunner.testCase.setPropertyValue("pagesAmount", Integer.toString(pagesAmount))
testRunner.testCase.setPropertyValue("currentPageNumber", "0")

//log.info "  Total Count Tenants: " + totalCountTenants
log.info "  Page Size: " + pageSize
log.info "  Pages Amount: " + pagesAmount

import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("GetAllTenantsSorted") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response


context.listTenantsObject = []

for(int i = 0; i < json.tenants.size(); i++) {

	def key = json.tenants[i].key
	def name = json.tenants[i].name
	def enabled = json.tenants[i].enabled

	context.listTenantsObject.add(new TenantObject(key, name, enabled))	
	// log.info " Tenant # " + (i+1)
	// context.listTenantsObject.get(i).showTenant(log)

}

public class TenantObject {
	
	def key = ""
	def name = ""
	def enabled = ""
	
	public TenantObject(def key, def name, def enabled) {
		
		this.key = key.trim()
		this.name = name.trim()
		this.enabled = enabled
		
	}

	public showTenant(log) {
		
		log.info "Tenant Key: " + key
		log.info "Tenant Name: " + name
		log.info "Enabled: " + enabled
		
	}
}

import groovy.json.JsonSlurper 
def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context ); 
def testCase = context.testCase; 
def testStep = testCase.getTestStepByName("SortTenants") 
def dataSourceSortBy = testCase.getTestStepByName("DataSource_SortCriteria") 
def dataSourcePage = testCase.getTestStepByName("DataSource_PageSize") 

// Get JSON response 
def response = testStep.testRequest.messageExchange.response.responseContent
def slurper = new JsonSlurper()
def json = slurper.parseText response

def sortDirection = dataSourceSortBy.getPropertyValue("sortDirection")
def sortBy = dataSourceSortBy.getPropertyValue("sortBy")
int pageSize = Integer.parseInt(dataSourcePage.getPropertyValue("pageSize"))
int page = Integer.parseInt(testCase.getPropertyValue("currentPageNumber"))

// Calculate indexes to get tenants from general list (list that contains all the tenants)
// Example: page = 2, pageSize = 5
// indexStart = (2-1) * 5 = 5
// indexEnd = 5 + 5 = 10 

int indexStart = (page - 1) * pageSize
int indexEnd = indexStart + pageSize - 1
int totalCount = Integer.parseInt(testRunner.testCase.getPropertyValue("totalCountTenants"))

if (indexEnd >  totalCount) indexEnd = totalCount - 1

//log.info "indexStart: " + indexStart
//log.info "indexEnd: " + indexEnd
log.info "  Sort By " + sortBy + " in " + sortDirection + " order"

int index = 0

for (int i = indexStart; i <= indexEnd; i++) {

	if (sortBy == "KEY"){

		assert(json.tenants[index].key.equals(context.listTenantsObject.get(i).key))	
		// log.info  "  " + json.tenants[index].key + " equals " + context.listTenantsObject.get(i).key
		/*
		try {
			
			assert(json.tenants[index].key.equals(context.listTenantsObject.get(i).key))	
		
		} catch (AssertionError e) {
		
			log.info "  Tenants didn't match on page " + page
			log.info "  Tenant key expected: " + context.listTenantsObject.get(i).key
			log.info "  Tenant key actual  : " + json.tenants[index].key
		
		}
		*/

	} else if (sortBy == "NAME"){

		String expected = context.listTenantsObject.get(i).name.trim()
		String actual = json.tenants[index].name.trim()

		assert(actual.equals(expected))	
		// log.info  "  " + actual + " equals " + expected
			
		/* try {
			assert(actual.equals(expected))			
		
		} catch (AssertionError e) {
		
			log.info "  Tenants didn't match on page " + page
			log.info "  Tenant name expected: " + context.listTenantsObject.get(i).name
			log.info "  Tenant name actual  : " + json.tenants[index].name
		
		}*/
		
	} else if (sortBy == "IS_ENABLED"){

		assert(json.tenants[index].enabled.equals(context.listTenantsObject.get(i).enabled))	
		// log.info  "  " + json.tenants[index].enabled + " equals " + context.listTenantsObject.get(i).enabled
		
		/*try {
		
			assert(json.tenants[index].enabled.equals(context.listTenantsObject.get(i).enabled))	
		
		} catch (AssertionError e) {
		
			log.info "  Tenants didn't match on page " + page
			log.info "  Enabled value expected: " + context.listTenantsObject.get(i).enabled
			log.info "  Enabled value actual  : " + json.tenants[index].enabled
		
		}*/
		
	} 
		
	index++
}
